uint8_t* retry_get_data(uint8_t* (*func)(int), int caller, int retries) {
    uint8_t *data = NULL;
    for (int i = 0; i < retries; ++i) {
        data = func(caller);
        if (data != NULL) {
            break;
        }
    }
    return data;
}

